import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const InvoiceDetails = () => {
  const { id } = useParams(); // Get the line number from the URL
  const [units, setUnits] = useState([]);
  const [invoice, setInvoice] = useState({
    id: id,
    productName: "",
    unitNo: "",
    price: 0,
    quantity: 0,
    total: 0,
    expiryDate: "",
  });

  useEffect(() => {
    const fetchUnits = async () => {
      const response = await axios.get("http://localhost:4000/api/units");
      setUnits(response.data);
    };

    const fetchInvoice = async () => {
      if (id) {
        const response = await axios.get(
          `http://localhost:4000/api/invoicedetails/${id}`
        ); // Adjust URL
        console.log("Fetched Invoice Data:", response.data);
        setInvoice(response.data);
      }
    };

    fetchUnits();
    fetchInvoice();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setInvoice((prevState) => ({
      ...prevState,
      [name]: name === "price" || name === "quantity" ? Number(value) : value,
      total:
        name === "price" || name === "quantity"
          ? Number(value) * prevState.quantity // or vice versa, depending on your logic
          : prevState.total,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    console.log("Submitting Invoice Data:", invoice);
    // Log the invoice data
    try {
      if (id) {
        // Update existing invoice
        await axios.put(
          `http://localhost:4000/api/invoicedetails/${id}`,
          invoice
        );
        alert("Invoice updated!");
      } else {
        // Create new invoice
        await axios.post(`http://localhost:4000/api/invoicedetails`, invoice);
        alert("Invoice created!");
      }
    } catch (error) {
      console.error("Error saving invoice:", error);
      alert("Error saving invoice. Please check the console for details.");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="number" name="id" value={id} />
      <input
        type="text"
        name="productName"
        value={invoice.productName}
        onChange={handleChange}
        required
      />
      <select
        name="unitNo"
        value={invoice.unitNo}
        onChange={handleChange}
        required
      >
        <option value="">Select Unit</option>
        {units.map((unit) => (
          <option key={unit.unitNo} value={unit.unitNo}>
            {unit.unitName}
          </option>
        ))}
      </select>
      <input
        type="number"
        name="price"
        value={invoice.price}
        onChange={handleChange}
        required
      />
      <input
        type="number"
        name="quantity"
        value={invoice.quantity}
        onChange={handleChange}
        required
      />
      <input
        type="date"
        name="expiryDate"
        value={invoice.expiryDate}
        onChange={handleChange}
        required
      />
      <button type="submit">Update Invoice</button>
    </form>
  );
};

export default InvoiceDetails;
