import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom"; // Change this line

const InvoiceList = () => {
  const [invoices, setInvoices] = useState([]);
  const navigate = useNavigate(); // Change this line

  useEffect(() => {
    // Fetching the invoices from the server
    const fetchInvoices = async () => {
      try {
        const response = await axios.get(
          "http://localhost:4000/api/invoicedetails"
        ); // Adjust this URL based on the API
        setInvoices(response.data);
      } catch (error) {
        console.error("Error fetching invoices:", error);
      }
    };
    fetchInvoices();
  }, []);

  return (
    <div>
      <h1>Invoice List</h1>
      <table>
        <thead>
          <tr>
            <th>id</th>
            <th>Product Name</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {invoices.map((invoice) => (
            <tr key={invoice.id || invoice._id}>
              <td>{invoice.id || invoice._id}</td>
              <td>{invoice.productName}</td>
              <td>
                <button
                  onClick={() =>
                    navigate(`/invoice-details/${invoice.id || invoice._id}`)
                  }
                >
                  View Details
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default InvoiceList;
